{{groovy}}
import net.fortuna.ical4j.model.*;
import net.fortuna.ical4j.data.*;
import net.fortuna.ical4j.model.property.*;
import net.fortuna.ical4j.model.component.*;
import net.fortuna.ical4j.model.parameter.*;
import net.fortuna.ical4j.util.*;
import org.xwiki.security.authorization.*;

def calendarReference = request.calendar
if (calendarReference == null) {
  println '{{error}}No "calendar" URL parameter has been provided{{/error}}'

  return;
}

if (xcontext.userReference == null) {
  // Since Google Calendar does not support basic auth and the only thing you can give is a URL we have workaround it with a crappy hack
  // Never ever use that if you are no behind SSL
  def user = request.user;
  if (user != null) {
    def password = request.password;
    def principal = xwiki.getXWiki().getAuthService().authenticate(user, password, xcontext.context);
    if (principal != null) {
      //request.getSession().setAttribute(java.security.Principal.PRINCIPAL_SESSION_KEY, principal);
      xcontext.context.setUser(principal.getName());
    }
  }
}

def calendarDocument = xwiki.getDocument(calendarReference)
if (calendarDocument == null || calendarDocument.isNew()) {
  println "{{error}}Cannot access calendar [[$services.rendering.escape($services.rendering.escape($calendarReference, 'xwiki/2.1'), 'xwiki/2.1')>>$services.rendering.escape($calendarReference, 'xwiki/2.1')]]{{/error}}";

  return ;
}

// Create the calendar
Calendar calendar = new Calendar();
calendar.getProperties().add(new ProdId("-//XWiki//iCal4j 1.0//EN"));
calendar.getProperties().add(Version.VERSION_2_0);
calendar.getProperties().add(CalScale.GREGORIAN);
calendar.getProperties().add(new XProperty("X-WR-CALNAME", calendarDocument.plainTitle));

// Add events
def events = services.query.xwql('from doc.object(MoccaCalendar.MoccaCalendarEventClass) as event where doc.parent = :parent').bindValue('parent', calendarReference).execute();

events.each {
  def eventDocument = xwiki.getDocumentAsAuthor(it);

  if (eventDocument != null) {
    def startDate = eventDocument.getValue('startDate');
    def endDate = eventDocument.getValue('endDate');

    VEvent event;
    if (endDate != null && eventDocument.getValue('allDay') != 1) {
      startDate = new DateTime(startDate);
      startDate.setUtc(true);
      endDate = new DateTime(endDate);
      endDate.setUtc(true);

      event = new VEvent(startDate, endDate, eventDocument.plainTitle);
    } else {
      // Go trough String to workaround any timezone issue
      // TODO: find a more elegant way
      def utcDate = new Date(startDate.format('yyyyMMdd'), 'yyyyMMdd')
      event = new VEvent(utcDate, eventDocument.plainTitle);
    }

    def description = eventDocument.getValue('description').replace('([^\\r])\\n', '$1\\r\\n');
    description += "\r\n\r\n";
    description += eventDocument.externalURL;
    event.getProperties().add(new Description(description));

    // Generate a UID for the event..
    UidGenerator ug = new FixedUidGenerator(new SimpleHostInfo('atelier-medias.org'), eventDocument.documentReference.toString());
    event.getProperties().add(ug.generateUid());
    calendar.getComponents().add(event)
  }
}

// Set mime type
response.setContentType('text/calendar');
// set file name
response.setHeader("Content-Disposition", "attachment; filename=${calendarReference}.ics");

// Send result
CalendarOutputter outputter = new CalendarOutputter();
outputter.output(calendar, response.outputStream);
response.flushBuffer();

// We are done
xcontext.setFinished(true)
{{/groovy}}

property.code:
require(['jquery', 'xwiki-events-bridge'], function($) {
  var iCalIcon = $([]);
  var iconURL = XWiki.contextPath + '/rest/wikis/' + XWiki.currentWiki + '/iconThemes/icons?name=download';
  $.getJSON(iconURL).then(function(result) {
    if (result.icons && result.icons.length) {
      var iconMetaData = result.icons[0];
      if (iconMetaData.cssClass) {
        iCalIcon = $('<span></span>').addClass(iconMetaData.cssClass);
      } else if (iconMetaData.url) {
        iCalIcon = $('<img/>').attr('src', iconMetaData.url);
      }
      $('.actionical .action-icon').append(iCalIcon.clone());
    }
  });

  var addiCalAction = function(data) {
    // Extend the Actions column with the iCal link.
    var calendarURL = new XWiki.Document('Service', 'MoccaiCalendar').getURL('get',
      'calendar=' + encodeURIComponent(data.data.doc_fullName));
    var iCalAction = $(
      '<a class="action actionical">' +
        '<span class="action-icon" title="iCal"></span>' +
        '<span class="action-label">iCal</span>' +
      '</a>'
    ).attr('href', calendarURL).appendTo($(data.row).find('.actions'));
    iCalAction.find('.action-icon').append(iCalIcon.clone());
  };

  var maybeAddiCalAction = function() {
    var calendarLiveTable = window['livetable_moccacalendar'];
    if (!calendarLiveTable || !calendarLiveTable.fetchedRows || !calendarLiveTable.fetchedRows.length) {
      // The calendar live table is missing or is not loaded yet or is empty.
      return;
    }
    $('#moccacalendar.xwiki-livetable .xwiki-livetable-display-body tr').each(function() {
      var rowIndex = parseInt($(this).attr('data-index'));
      addiCalAction({
        data: calendarLiveTable.fetchedRows[rowIndex],
        row: this,
        table: calendarLiveTable
      });
    });
  };

  $(document).on('xwiki:livetable:moccacalendar:newrow', function(event, data) {
    addiCalAction(data);
  });

  $(maybeAddiCalAction);
});
